        </Link>
        <Link id="cubic" to="/signup">
          {" "}
          <span title="Sign Up"> Sign Up </span>{" "}
        </Link>
        <Link id="cubic" to="/about">
          {" "}
          <span title="About"> About </span>{" "}
        </Link>
      </div>
    </div>
  );
}

export default Nav;
